<?phpclass Theappnow_Baseprice_Block_Adminhtml_Price_Renderer_Category extends Mage_Adminhtml_Block_Widget_Grid_Column_Renderer_Abstract{    public function render(Varien_Object $row)    {        $value = $row->getData($this->getColumn()->getIndex());        $Category = Mage::getModel('catalog/category')->load((int)$value);        return $Category->getName();    }}?>