<?phpclass Theappnow_Baseprice_Block_Adminhtml_Price_Edit_Tabs extends Mage_Adminhtml_Block_Widget_Tabs{    public function __construct()    {        parent::__construct();        $this->setId('baseprice_tabs');        $this->setDestElementId('edit_form');        $this->setTitle(Mage::helper('baseprice')->__('Baseprice Information'));    }    protected function _beforeToHtml()    {        $this->addTab('form_section', array(            'label' => Mage::helper('baseprice')->__('Baseprice Information'),            'title' => Mage::helper('baseprice')->__('Baseprice Information'),            'content' => $this->getLayout()                ->createBlock('baseprice/adminhtml_price_edit_tab_form')                ->toHtml(),        ));        return parent::_beforeToHtml();    }}?>