<?phpclass Theappnow_Baseprice_Block_Adminhtml_Tierprice_Edit_Tab_Form extends Mage_Adminhtml_Block_Widget_Form{    protected function _prepareForm()    {        $form = new Varien_Data_Form();        $this->setForm($form);        if (Mage::getSingleton('adminhtml/session')->getBasepriceData()) {            $data = Mage::getSingleton('adminhtml/session')->getBasepriceData();            Mage::getSingleton('adminhtml/session')->getBasepriceData(null);        } elseif (Mage::registry('baseprice_tierpricedata')) {            $data = Mage::registry('baseprice_tierpricedata')->getData();        }        $fieldset = $form->addFieldset('baseprice_form', array(            'legend' => Mage::helper('baseprice')->__('Tierprice information')        ));        $fieldset->addField('level', 'text', array(            'label' => Mage::helper('baseprice')->__('Level'),            'class' => 'required-entry',            'required' => true,            'name' => 'level',        ));        $fieldset->addField('qty', 'text', array(            'label' => Mage::helper('baseprice')->__('Qty'),            'class' => 'required-entry',            'required' => true,            'name' => 'qty',        ));        $fieldset->addField('price_deviation', 'text', array(            'label' => Mage::helper('baseprice')->__('Price deviation'),            'class' => 'required-entry',            'required' => true,            'name' => 'price_deviation',        ));        $form->setValues($data);        return parent::_prepareForm();    }}?>